pragma solidity >=0.4.25 <0.6.0;

contract News {
    address sender;
    string titre;
    string description;
    uint256 vote;

    function fakeNews(
        string memory _titre,
        string memory _description,
        uint256 _vote
    ) public {
        sender = msg.sender;
        titre = _titre;
        description = _description;
        vote = _vote;
    }

    function getNews()
        public
        view
        returns (
            address _sender,
            string memory _titre,
            string memory _description,
            uint256 _vote
        )
    {
        return (sender, titre, description, vote);
    }
}
